window.onload=function(e){$("signin-form").addEventListener("submit",login)};function login(e){e.preventDefault();var u=$("username").value;var p=$("password").value;call("POST","api/auth/logon","username="+u+"&password="+p,function(e,success){if(!success){show($("login-fail"));return}if(e.indexOf("401")===-1){location.href="home.jsp"}else{show($("login-fail"))}},1)};