window.onload=function(e){$("signin-form").addEventListener("submit",svalidate)};function svalidate(e){e.preventDefault();var data="username="+$("username").value+"&password="+$("inputPassword").value;call("POST","api/sessions/logon?"+data,null,function(e,success){if(!success){show($("login-fail"))}else{if(e==""){show($("login-fail"))}else{console.log(e);getOATH()}}})}function getOATH(){call("POST","api/sessions/whoami",null,function(e,success){console.log(e)})}function logout(){call("POST","api/sessions/logout",null,function(e,success){})};